mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select d.*, c.name as 'categoryName' from dish as d left join category as c on d.category_id = c.id


        and d.name like concat('%', #{dishPageQueryDTO.name}, '%')


        and d.categoryId = #{dishPageQueryDTO.categoryId}


        and d.status = #{dishPageQueryDTO.status}


order by d.create_time desc limit #{offset}, #{dishPageQueryDTO.pageSize}
